filters.fragment:

precision highp float;
uniform sampler2D inputImageTexture; //video
uniform sampler2D inputImageTexture2; //mv
uniform sampler2D inputImageTexture3; //alpha
varying vec2 textureCoordinate;
void main()
{
vec4 video = texture2D(inputImageTexture, textureCoordinate);
vec4 mv    = texture2D(inputImageTexture2, textureCoordinate);
vec4 alpha = texture2D(inputImageTexture3, textureCoordinate);
gl_FragColor = video * (1.0 - alpha.r) + mv;
}


precision highp float;
uniform sampler2D inputImageTexture; //video
uniform sampler2D inputImageTexture2; //screen
varying vec2 textureCoordinate;
void main()
{
vec4 video = texture2D(inputImageTexture, textureCoordinate);
vec4 screen = texture2D(inputImageTexture2, textureCoordinate);
mediump vec4 whiteColor = vec4(1.0);
gl_FragColor = whiteColor - ((whiteColor - screen) * (whiteColor - video));
}
